package HiWorld;

public class swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a,b;
a=7;
b=10;
System.out.println("Number before swap: a,b= "+a+", "+ b);
a = a + b;
b = a - b;
a = a - b;
System.out.println("Number after swap: a,b= "+a+", " +b);



	}

}
